import { Component } from "react";
import React from "react";

class Content extends Component {

  state = {
    loading: true
  }


  render() {
    return (
      <div>
        <div>Please select your card type.
          <select onChange={debitorcredit}>
          <option value="">-</option>
            <option value="credit">Credit</option>
            <option value="debit">Debit</option>
          </select>
          <table>
            <thead>
              <tr>
                 <th>Date</th>
                 <th>Transaction</th>
                 <th>Amount</th>
               </tr>
             </thead>
             <tbody id="myTable">
             </tbody>
            </table>
        </div>
      </div>
    );
  }
  async componentDidMount() {
    const debiturl = "https://moj-api.herokuapp.com/debits";
    const crediturl = "https://moj-api.herokuapp.com/credits";
    const debitresponse = await fetch(debiturl);
    const debitdata = await debitresponse.json();
    const creditresponse = await fetch(crediturl);
    const creditdata = await creditresponse.json();

    //should sort them by newest transactions to oldest
    function custom_sort(a, b) {
      return new Date(b.date).getTime() - new Date(a.date).getTime();
    };
    console.log(debitdata.sort(custom_sort));
    console.log(creditdata.sort(custom_sort));

	  function buildTable(data){
		  var table = document.getElementById('myTable');

		  for (var i = 0; i < data.length; i++){
		  	var row = `<tr>
							<td>${data[i].date.substr(0, 10)}</td>
              <td>${data[i].description}</td>
							<td>${data[i].amount}</td>
					  </tr>`
		    	table.innerHTML += row;


	  	}
	  }
    function debitorcredit(input){
      if(input == "debit"){
        buildTable(debitdata.sort(custom_sort));
      }
      if(input == "credit"){
        buildTable(creditdata.sort(custom_sort));
      }
      else{
        alert("hell nah");
      }
    }


  }
}


export default Content;
